hooks.hook:
// Subscript Tag
$tags[] = 'sub';
$tags_opened[] = 'sub';
$tags_closed[] = 'sub';
$tags_fix[] = 'sub';

// Superscript Tag
$tags[] = 'sup';
$tags_opened[] = 'sup';
$tags_closed[] = 'sup';
$tags_fix[] = 'sup';

// Strike Tag
$tags[] = 'strike';
$tags_opened[] = 'strike';
$tags_closed[] = 'strike';
$tags_fix[] = 'strike';

$tags_limit_bbcode['*'][] = 'strike';

// Line Tag
$tags[] = 'line';

// Subscript Tag
$pattern[] = '#\[sub\](.*?)\[/sub\]#ms';
$replace[] = '<sub>$matches[1]</sub>';

// Superscript Tag
$pattern[] = '#\[sup\](.*?)\[/sup\]#ms';
$replace[] = '<sup>$matches[1]</sup>';

// Strike Tag
$pattern[] = '#\[strike\](.*?)\[/strike\]#ms';
$replace[] = '<del>$matches[1]</del>';

// Line Tag
$pattern[] = '#\[line\]#ms';
$replace[] = '<hr />';
